class NewsUploadHistory {
  final String newsId;
  final List<String> thumbnailUrls;
  final String title;
  final String author;
  final String uploadedBy;
  final String time;
  final String status;
  final String description;
  final String newsPlain;

  NewsUploadHistory({
    required this.newsId,
    required this.thumbnailUrls,
    required this.title,
    required this.author,
    required this.uploadedBy,
    required this.time,
    required this.status,
    required this.description,
    required this.newsPlain,
  });

  factory NewsUploadHistory.fromMap(Map<String, dynamic> map) {
    return NewsUploadHistory(
      newsId: map['id'].toString(),
      thumbnailUrls: NewsUploadHistory.parseThumbnails(map['thumbnail']),
      title: map['subject'] ?? '',
      author: map['author'] ?? '',
      uploadedBy: map['created_by']?.toString() ?? '',
      time: map['news_date'] ?? '',
      status: (map['is_active'] ?? false) ? 'Active' : 'Inactive',
      description: map['short_description'] ?? '',
      newsPlain: map['news_plain'] ?? '',
    );
  }

  static List<String> parseThumbnails(dynamic value) {
    if (value is List) {
      return value.map((e) => e.toString()).toList();
    } else if (value is String) {
      if (value.contains(',')) {
        return value.split(',').map((e) => e.trim()).toList();
      } else if (value.isNotEmpty) {
        return [value];
      }
    }
    return [];
  }
}



class Country {
  final int id;
  final String nicename;
  final String iso;
  final String phoneCode;
  final String flagUrl;
  final String countryTag;

  Country({
    required this.id,
    required this.nicename,
    required this.iso,
    required this.phoneCode,
    required this.flagUrl,
    required this.countryTag,
  });

  factory Country.fromJson(Map<String, dynamic> json) {
    return Country(
      id: json['id'],
      nicename: json['nicename'] ?? '',
      iso: json['iso'] ?? '',
      phoneCode: json['phonecode']?.toString() ?? '',
      flagUrl: json['flagsUrl'] ?? '',
      countryTag: json['countryTag'] ?? '',
    );
  }

  // Override equality to allow comparison by id
  @override
  bool operator ==(Object other) =>
      identical(this, other) ||
          other is Country && runtimeType == other.runtimeType && id == other.id;

  @override
  int get hashCode => id.hashCode;
}


class TrendingColor {
  final Color color;
  final String hexCode;
  final String name;

  TrendingColor({
    required this.color,
    required this.hexCode,
    required this.name,
  });
}

String colorToHex(Color color) {
  return '#${color.value.toRadixString(16).padLeft(8, '0').toUpperCase()}';
}





